from typing import List
from collections import deque

class Solution:
    def openLock(self, deadends: List[str], target: str) -> int:

        queue = deque()
        # the lock initially starts at 0000
        queue.append("0000")

        tried = set(deadends)

        movement = 0

        while len(queue) > 0:

            movement += 1

            for _ in range(len(queue)):
                combination = queue.popleft()

                if combination in deadends:
                    continue

                for position in range(4):
                    for direction in (-1, 1):
                        newTry = list(combination)
                        newTry = combination[:position] + str((int(combination[position]) + direction) % 10) + combination[position+1:]

                        if newTry in tried:
                            continue

                        if newTry == target:
                            return movement

                        if newTry not in deadends and newTry not in tried:
                            queue.append(newTry)
                            tried.add(newTry)

        return -1

